mapper.select:
SELECT q.id AS questionId, q.content, q.correct_option AS correctOption,
       a.selected_option AS selectedOption
FROM questions q
         LEFT JOIN answers a ON q.id = a.question_id AND a.test_id = #{testId} AND a.student_username = #{studentUsername}
WHERE q.test_id = #{testId}

SELECT question_id AS questionId, topic_id AS topicId, content, option_a AS optionA, option_b AS optionB, option_c AS optionC, option_d AS optionD, correct_option AS correctOption, difficulty_id AS difficultyId, created_by AS createdBy, source FROM questions WHERE topic_id IN
#{id}
ORDER BY RANDOM() LIMIT #{limit}
