mapper.select:
SELECT s.student_id AS studentId, s.full_name AS fullName, s.dob, s.gender, s.email, c.course_name AS courseName, s.status FROM students s JOIN courses c ON s.course_id = c.course_id where 1= 1
AND (LOWER(s.student_id) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(s.full_name) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(s.email) LIKE '%' || LOWER(#{keyword}) || '%')
AND s.status = #{status}
AND c.course_name = #{courseName}
ORDER BY s.student_id LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM students s JOIN courses c ON s.course_id = c.course_id WHERE 1=1
AND ( LOWER(s.student_id) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(s.full_name) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(s.email) LIKE '%' || LOWER(#{keyword}) || '%' )
AND s.status = #{status}
AND c.course_name = #{courseName}

